import React, { useState, useEffect } from "react";
import Team from "./team.js";

const API_BASE = "https://api.sleeper.app/v1";

// Main App Component
const App = () => {
  const [username, setUsername] = useState("");
  const [inputUsername, setInputUsername] = useState("");
  const [leagues, setLeagues] = useState([]);
  const [teams, setTeams] = useState([]);
  const [selectedLeague, setSelectedLeague] = useState(null);
  const [leagueId, setLeagueId] = useState(null);
  const [userData, setUserData] = useState(null);
  const [userId, setUserId] = useState(null);
  const [leagueData, setLeagueData] = useState(null);

  useEffect(() => {
    if (username) {
      getUserData();
    }
  }, [username]);

  useEffect(() => {
    if (selectedLeague) {
      getSelectedLeague();
    }
  }, [selectedLeague]);

  const getUserData = async (username) => {
    const userDataResponse = await fetch(`${API_BASE}/user/${username}`);
    const userData = await userDataResponse.json();
    setUserData(userData);
    setUserId(userData.user_id);

    const leaguesDataResponse = await fetch(
      `${API_BASE}/user/${userData.user_id}/leagues/nfl/2023`
    );
    const leaguesData = await leaguesDataResponse.json();
    setLeagues(leaguesData);
  };

  const getSelectedLeague = async (leagueId) => {};

  const handleUsernameChange = (event) => {
    setInputUsername(event.target.value);
  };

  const handleConfirmClick = (event) => {
    setUsername(event.target.value);
  };

  const handleLeagueChange = (event) => {
    setSelectedLeague(event.target.value);
  };

let optionElements = [];

leagues.forEach((league) => {
  optionElements.push(
    <option key={league.id} value={league.id}>
      {league.name}
    </option>
  );
});

// then in the render method:
<select id={leagues} onChange={handleLeagueChange}>
  {optionElements}
</select>

  return (
    <div>
      <input
        type="text"
        value={inputUsername}
        onChange={handleUsernameChange}
      />
      <button onClick={handleConfirmClick}>Confirm</button>
      <select id={leagues} onChange={handleLeagueChange}>
        {leagues.map((league) => (
          <option key={league.id} value={league.id}>
            {league.name}
          </option>
        ))}
      </select>
      {teams.map((team) => (
        <Team key={team.id} team={team} />
      ))}
    </div>
  );
};

export default App;
