import React, { useState, useEffect } from "react";
import Team from "./team.js";

const API_BASE = "https://api.sleeper.app/v1";

// Main App Component
const App = () => {
  const [username, setUsername] = useState("");
  const [inputUsername, setInputUsername] = useState("");
  const [leagues, setLeagues] = useState([]);
  const [teams, setTeams] = useState([]);
  const [selectedLeague, setSelectedLeague] = useState(null);
  const [leagueId, setLeagueId] = useState(null);
  const [userData, setUserData] = useState(null);
  const [leagueData, setLeagueData] = useState(null);

  useEffect(() => {
    if (username) {
      getUserData(username);
    }
  }, [username]);

  useEffect(() => {
    if (selectedLeague) {
      getSelectedLeague();
    }
  }, [selectedLeague]);

  const getUserData = async (username) => {
    console.log(username);
    const userDataResponse = await fetch(`${API_BASE}/user/${username}`);
    const userData = await userDataResponse.json();
    console.log(`THIS IS USER DATA PART`, userData);
    setUserData(userData);
    console.log(userData.user_id);
    const leaguesResponse = await fetch(
      `${API_BASE}/user/${userData.user_id}/leagues/nfl/2023`
    );
    const leaguesData = await leaguesResponse.json();
    const leagueList = leaguesData.map((league) => {
      return { leagueName: league.name, leagueId: league.league_id };
    });
    setLeagues(leagueList);
    console.log(leagueList);
  };

  const getSelectedLeague = async (leagueId) => {};


const LeagueList = ({ leagues }) => {
  return (
    <div>
      <h2>League List:</h2>
      <select>
        {leagues.map((league) => (
          <option key={league.leagueId} value={league.leagueId}>
            {league.leagueName}
          </option>
        ))}
      </select>
    </div>
  );
};

  const listLeagues = async (leagues) => {
    const leagueList = leagues.map((league) => {
      return {
        name: league.name,
        leagueId: league.league_id,
      };
    });
  };

  const handleUsernameChange = (event) => {
    setInputUsername(event.target.value);
  };

  const handleConfirmClick = (event) => {
    setUsername(inputUsername);
  };

  const handleLeagueChange = (event) => {
    setSelectedLeague(event.target.value);
  };

  return (
    <div>
      <input
        type="text"
        value={inputUsername}
        onChange={handleUsernameChange}
      />
      <button onClick={handleConfirmClick}>Confirm</button>
      <div>
        <label htmlFor="leagues">Select a League:</label>
        <LeagueList leagues={leagues} />
      </div>
    </div>
  );
};

export default App;
