import LeagueSelection from "./SelectLeague";

export default function Home({
    inputUsername,
    handleUsernameChange,
    handleConfirmUser,
    username,
    leagues,
    handleLeagueChange,
    handleLeagueConfirm,
    confirmedLeague,
    handleShowRosters,
    handleLeagueButton,
    selectedLeague, 
}) {
    return (
      <div className="home">
        {username ? (
          <>
            <h2>Welcome, {username}</h2>
            {!confirmedLeague && (
              <LeagueSelection
                leagues={leagues}
                handleLeagueChange={handleLeagueChange}
                handleLeagueConfirm={handleLeagueConfirm}
              />
            )}
            {confirmedLeague && (
              <>
              <h2>Current League : {selectedLeague.name}</h2>
                <button onClick={handleShowRosters} className="league-button">
                  Show Rosters
                </button>
                <button onClick={handleLeagueButton} className="league-button">
                  Trade Analyzer
                </button>
              </>
            )}
          </>
        ) : (
          <>
            <h2>Welcome</h2>
            <div className="input-group">
              <input
                type="text"
                value={inputUsername}
                onChange={handleUsernameChange}
                className="username-input"
                placeholder="username"
              />
              <button onClick={handleConfirmUser}>Confirm</button>
            </div>
          </>
        )}
      </div>
    );
}
