//   player,
//   onSelect,
//   isSelected,
//   playerBelongsToUser,
// }) => (
//   <button
//     onClick={() => onSelect(player)}
//     style={{ backgroundColor: isSelected ? "green" : "grey" }}
//   >
//     {player.name}
//   </button>
// );

export default TradeAnalyzer;
