import React, { useState } from "react";

const TradeAnalyzer = ({ rosters, currentOwnerId }) => {
  const [state, setState] = useState({
    stage: "select-team",
    selectedTeam: null,
    selectedPlayer: null,
    selectedPlayers: [],
    userTeam: rosters.find(roster => roster.owner_id === currentOwnerId),
    userSelectedPlayers: []
  });

  const handleTeamSelect = (team) => {
    setState(prevState => ({ ...prevState, stage: "select-player", selectedTeam: team }));
  };

  const handlePlayerSelect = (player) => {
    setState(prevState => ({ ...prevState, selectedPlayers: [...prevState.selectedPlayers, player] }));
  };

  const handleUserPlayerSelect = (player) => {
    setState(prevState => ({ ...prevState, userSelectedPlayers: [...prevState.userSelectedPlayers, player] }));
  };

  const finishSelecting = () => {
    setState(prevState => ({ ...prevState, stage: "done" }));
  }

  const finishUserSelecting = () => {
    setState(prevState => ({ ...prevState, stage: "compare" }));
  }

  const comparePlayers = () => {
    const totalTradeValueSelected = state.selectedPlayers.reduce((sum, player) => sum + player.tradeValue, 0);
    const totalTradeValueUser = state.userSelectedPlayers.reduce((sum, player) => sum + player.tradeValue, 0);
    return totalTradeValueSelected - totalTradeValueUser;
  }

  return (
    <div>
      {state.stage === "select-team" &&
        rosters.map((team) => (
          <TeamButton key={team.id} team={team} onSelect={handleTeamSelect} />
        ))}

      {state.stage === "select-player" &&
        state.selectedTeam.roster.map((player) => (
          <PlayerButton key={player.id} player={player} onSelect={handlePlayerSelect} />
        ))}

      {state.stage === "select-player" && (
        <button onClick={finishSelecting}>Done</button>
      )}

      {state.stage === "done" &&
        state.userTeam.roster.map((player) => (
          <PlayerButton key={player.id} player={player} onSelect={handleUserPlayerSelect} />
        ))}

      {state.stage === "done" && (
        <button onClick={finishUserSelecting}>Done</button>
      )}

      {state.stage === "compare" && (
        <p>The difference in trade value is: {comparePlayers()}</p>
      )}

      <h2>Selected Players</h2>
      {state.selectedPlayers.map((player, index) => (
        <p key={index}>
          {player.name} - {player.tradeValue}
        </p>
      ))}

      <h2>User Selected Players</h2>
      {state.userSelectedPlayers.map((player, index) => (
        <p key={index}>
          {player.name} - {player.tradeValue}
        </p>
      ))}
    </div>
  );
};

const TeamButton = ({ team, onSelect }) => (
  <button onClick={() => onSelect(team)}>
    {team.teamName}
  </button>
);

const PlayerButton = ({ player }) => {
  const [isSelected, setIsSelected] = useState(false);

  const handleSelect = () => {
    setIsSelected(!isSelected);
  };

  return (
    <button 
      onClick={handleSelect} 
      style={{ backgroundColor: isSelected ? 'green' : 'grey' }}
    >
      {player.name}
    </button>
  );
};

export default TradeAnalyzer;
