import React, { useState, useEffect } from "react";
import {
  createBrowserRouter,
  Routes,
  Route,
  Link,
  NavLink,
  createRoutesFromElements,
  RouterProvider
} from "react-router-dom";
import "./app.css";

import Home from "./pages/Home";
import About from "./pages/About";
import RootLayout from "./layouts/RootLayout";


class Team {
  constructor(tName, owner) {
    this.teamName = tName || "";
    this.owner_id = owner;
    this.roster = [];
    this.totalPts = 0;
    this.totalWeekly = 0;
    this.totalTradeValue = 0;
  }
}

const API_BASE = "https://api.sleeper.app/v1";

const router = createBrowserRouter(
  createRoutesFromElements(
    <Route path="/" element = {<RootLayout />}>
      <Route index element = {<Home input/>} /> 
      <Route path="about" element={<About />} /> 
    </Route>
  )
)

// Main App Component
const App = () => {
  const [username, setUsername] = useState("");
  const [inputUsername, setInputUsername] = useState("");
  const [leagues, setLeagues] = useState([]);
  const [leagueTeams, setLeagueTeams] = useState([]);
  const [selectedLeague, setSelectedLeague] = useState(null);
  const [userData, setUserData] = useState(null);
  const [leagueUsers, setLeagueUsers] = useState(null);
  const [rosters, setRosters] = useState(null);
  const [confirmedLeague, setConfirmedLeague] = useState(false);
  const [rosterDisplay, setRosterDisplay] = useState(null);
  const [showTradeAnalyzer, setShowTradeAnalyzer] = useState(false);


  useEffect(() => {
    if (username) {
      fetchUserData(username);
      let test = new Team("gg", "234");
    }
  }, [username]);

  useEffect(() => {
    if (selectedLeague) {
      fetchLeagueDetails(selectedLeague.league_id);
    }
  }, [selectedLeague]);

  const fetchUserData = async (username) => {
    const userDataResponse = await fetch(`${API_BASE}/user/${username}`);
    const userData = await userDataResponse.json();
    const leaguesResponse = await fetch(
      `${API_BASE}/user/${userData.user_id}/leagues/nfl/2023`
    );
    const leaguesData = await leaguesResponse.json();

    setLeagues(leaguesData);
  };

  const handleUsernameChange = (event) => {
    setInputUsername(event.target.value);
  };

  const handleConfirmUser = () => {
    setUsername(inputUsername);
  };

  const handleLeagueChange = (event) => {
    const leagueId = event.target.value;
    const selectedLeague = leagues.find(
      (league) => league.league_id === leagueId
    );
    setSelectedLeague(selectedLeague);
  };

  const fetchLeagueDetails = async (leagueId) => {
    console.log(leagueId);
    const leagueUsersResponse = await fetch(
      `${API_BASE}/league/${leagueId}/users`
    );
    const leagueUsers = await leagueUsersResponse.json();
    setLeagueUsers(leagueUsers);
    console.log(leagueUsers);

    const leagueTeamsResponse = await fetch(
      `${API_BASE}/league/${leagueId}/rosters`
    );
    const leagueTeams = await leagueTeamsResponse.json();
    setLeagueTeams(leagueTeams);
    console.log(leagueTeams);
    const rosters = await constructTeams(leagueUsers, leagueTeams);
    setRosters(rosters);
    console.log(rosters);
  };

  const constructTeams = async (leagueUsers, leagueTeams) => {
    if (!leagueUsers || !leagueTeams) {
      console.error("ERROR LEAGUE UERS OR LEAGUE TEAMS IS NULL OR UNDEFINED");
      return;
    }
    const teams = leagueUsers.map(async (userTeam) => {
      const team = new Team(userTeam.display_name, userTeam.user_id);
      const roster = leagueTeams.find(
        (roster) => roster.owner_id === userTeam.user_id
      );
      let errorOccurred = false;

      if (!Array.isArray(roster.players)) {
        console.log("not an array");
        return;
      }

      for (const newPlayer of roster.players) {
        let player = await getPlayerData(newPlayer);

        if (!player) {
          console.error(`No player found`);
          errorOccurred = true;
          break;
        } else {
          team.roster.push(player);
        }
      }

      return team;
    });

    return Promise.all(teams);
  };

  async function getPlayerData(playerId) {
    try {
      const response = await fetch(
        `http://localhost:8080/api/nfl/player/${playerId}`
      );
      const playerData = await response.json();
      if (playerData.err) {
        console.error("Error: " < playerData.err);
      } else {
        return playerData;
      }
    } catch (error) {
      console.error("Error: ", error);
    }
  }

  const printRosters = (rosters) => {
    if (!rosters) {
      return <p>No rosters to display</p>;
    }

    return rosters.map((team, teamIndex) => (
      <div key={"team-" + teamIndex}>
        <h2>Team: {team.teamName}</h2>
        {team.roster
          .sort((a, b) => b.tradeValue - a.tradeValue)
          .map((player, playerIndex) => (
            <div key={"player-" + playerIndex} className="playerDetails">
              <span>{player.position}</span>
              <span>{player.name}</span>
              <span>Trade Value: {player.tradeValue}</span>
            </div>
          ))}
      </div>
    ));
  };

  const handleLeagueConfirm = () => {
    setConfirmedLeague(true);
  };

  const handleShowRosters = () => {
    setRosterDisplay(printRosters(rosters));
  };

  const TeamButton = ({ team, onSelect }) => (
    <button onClick={() => onSelect(team)}>{team.teamName}</button>
  );

  const PlayerButton = ({ player, onSelect }) => (
    <button onClick={() => onSelect(player)}>{player.name}</button>
  );

  const TradeAnalyzer = ({ rosters }) => {
    const [state, setState] = useState({
      stage: "select-team",
      selectedTeam: null,
      selectedPlayers: [],
    });

    const handleTeamSelect = (team) => {
      setState({ stage: "select-player", selectedTeam: team });
    };

    const handlePlayerSelect = (player) => {
      setState({ stage: "done", selectedPlayer: player });
    };

    return (
      <div>
        <button
          onClick={() =>
            setState({
              stage: "select-team",
              selectedTeam: null,
              selectedPlayer: null,
            })
          }
        >
          Trade Analyzer
        </button>

        {state.stage === "select-team" &&
          rosters.map((team) => (
            <TeamButton key={team.id} team={team} onSelect={handleTeamSelect} />
          ))}

        {state.stage === "select-player" &&
          state.selectedTeam.roster.map((player) => (
            <PlayerButton
              key={player.id}
              player={player}
              onSelect={handlePlayerSelect}
            />
          ))}

        {state.stage === "done" && (
          <div>Selected player: {state.selectedPlayer.name}</div>
        )}
      </div>
    );
  };

  const handleLeagueButton = (event) => {
    setShowTradeAnalyzer(true);
  };

  const router = createBrowserRouter(
    createRoutesFromElements(
      <Route path="/" element = {<RootLayout />}>
        <Route index element = { <Home 
      inputUsername={inputUsername} 
      handleUsernameChange={handleUsernameChange}
      handleConfirmUser={handleConfirmUser}
    />} /> 
        <Route path="about" element={<About />} /> 
      </Route>
    )
  )

  return (
    <RouterProvider router={router} />
    // <div className="app">
    //   <div className="input-group">
    //     <input
    //       type="text"
    //       value={inputUsername}
    //       onChange={handleUsernameChange}
    //     />
    //     <button onClick={handleConfirmUser}>Confirm</button>
    //   </div>

    //   {leagues.length > 0 && (
    //     <div>
    //       <label htmlFor="leagues">Select a League:</label>
    //       <select onChange={handleLeagueChange}>
    //         {leagues.map((league) => (
    //           <option key={league.league_id} value={league.league_id}>
    //             {league.name}
    //           </option>
    //         ))}
    //       </select>
    //       <button onClick={handleLeagueConfirm}>Confirm League</button>
    //       {confirmedLeague && (
    //         <button onClick={handleShowRosters}>Show Rosters</button>
    //       )}
    //       {confirmedLeague && (
    //         <button onClick={handleLeagueButton}>Start Trade Analysis</button>
    //       )}
    //     </div>
    //   )}

    //   {showTradeAnalyzer && (
    //     <div className="trade-analyzer">
    //       <TradeAnalyzer rosters={rosters} />
    //     </div>
    //   )}

    //   {rosterDisplay}
    // </div>
  );
};
export default App;

export const inputUsername = ''; // Assuming it's already defined in your code
export const handleUsernameChange = (event) => {
  // Assuming it's already defined in your code
};
export const handleConfirmUser = () => {
  // Assuming it's already defined in your code
};

// Other code in your App.js file
