import React, { useState } from "react";

const TradeAnalyzer = ({ rosters, currentOwnerId }) => {
    const [state, setState] = useState({
      stage: "select-team",
      selectedTeam: null,
      selectedPlayers: [],
      userTeam: rosters.find((roster) => roster.owner_id === currentOwnerId),
      userSelectedPlayers: [],
    });
  
    const handleTeamSelect = (team) => {
      setState((prevState) => ({
        ...prevState,
        stage: "select-players",
        selectedTeam: team,
      }));
    };
  
  const handlePlayerSelect = (player) => {
    if (state.selectedPlayers.some((p) => p.player_id === player.player_id)) {
      setState((prevState) => ({
        ...prevState,
        selectedPlayers: prevState.selectedPlayers.filter(
          (p) => p.player_id !== player.player_id
        ),
      }));
    } else {
      setState((prevState) => ({
        ...prevState,
        selectedPlayers: [...prevState.selectedPlayers, player],
      }));
    }
  };

  const handleUserPlayerSelect = (player) => {
    if (
      state.userSelectedPlayers.some((p) => p.player_id === player.player_id)
    ) {
      setState((prevState) => ({
        ...prevState,
        userSelectedPlayers: prevState.userSelectedPlayers.filter(
          (p) => p.player_id !== player.player_id
        ),
      }));
    } else {
      setState((prevState) => ({
        ...prevState,
        userSelectedPlayers: [...prevState.userSelectedPlayers, player],
      }));
    }
  };

  const finishSelecting = () => {
    setState((prevState) => ({ ...prevState, stage: "done" }));
  };

  const finishUserSelecting = () => {
    setState((prevState) => ({ ...prevState, stage: "compare" }));
  };

  const comparePlayers = () => {
    const totalTradeValueSelected = state.selectedPlayers.reduce(
      (sum, player) => sum + player.tradeValue,
      0
    );
    const totalTradeValueUser = state.userSelectedPlayers.reduce(
      (sum, player) => sum + player.tradeValue,
      0
    );
    return totalTradeValueSelected - totalTradeValueUser;
  };

  const calculateTotalTradeValue = (players) => {
    return players.reduce((total, player) => total + player.tradeValue, 0);
  };
  let totalTradeValueForSelectedTeam = 0;
  let totalTradeValueForUserTeam = 0;

  if (state.selectedPlayers) {
    totalTradeValueForSelectedTeam = calculateTotalTradeValue(
      state.selectedPlayers
    );
  }
  if (state.userSelectedPlayers) {
    totalTradeValueForUserTeam = calculateTotalTradeValue(
      state.userSelectedPlayers
    );
  }

  // Now you can use totalTradeValueForSelectedTeam and totalTradeValueForUserTeam in your return statement

  return (
    <div>
      {state.stage === "select-team" && (
        <>
          <h2>Your Team</h2>
          {state.userTeam.roster.map((player) => (
            <PlayerButton
              key={player.player_id}
              player={player}
              onSelect={handleUserPlayerSelect}
              isSelected={
                state.userSelectedPlayers.some(
                  (p) => p.player_id === player.player_id
                )
              }
            />
          ))}
          <h2>Select a Team to Trade With</h2>
          {rosters
            .filter((team) => team.owner_id !== currentOwnerId)
            .map((team) => (
              <TeamButton
                key={team.owner_id}
                team={team}
                onSelect={handleTeamSelect}
              />
            ))}
        </>
      )}

      {state.stage === "select-players" && (
        <>
          <h2>Your Team</h2>
          {state.userTeam.roster.map((player) => (
            <PlayerButton
              key={player.player_id}
              player={player}
              onSelect={handleUserPlayerSelect}
              isSelected={
                state.userSelectedPlayers.some(
                  (p) => p.player_id === player.player_id
                )
              }
            />
          ))}
          <h2>Selected Team</h2>
          {state.selectedTeam.roster.map((player) => (
            <PlayerButton
              key={player.player_id}
              player={player}
              onSelect={handlePlayerSelect}
              isSelected={
                state.selectedPlayers.some(
                  (p) => p.player_id === player.player_id
                )
              }
            />
          ))}
          <button onClick={finishSelecting}>Done</button>
        </>
      )}
      {state.stage === "compare" && (
        <p>The difference in trade value is: {comparePlayers()}</p>
      )}

      <h2>Selected Players</h2>
      <h2>Total Trade Value: {totalTradeValueForSelectedTeam}</h2>
      {state.selectedPlayers.map((player, index) => (
        <p key={index}>
          {player.name} - {player.tradeValue}
        </p>
      ))}

      <h2>User Selected Players</h2>
      <h2>Total Trade Value: {totalTradeValueForUserTeam}</h2>
      {state.userSelectedPlayers.map((player, index) => (
        <p key={index}>
          {player.name} - {player.tradeValue}
        </p>
      ))}
    </div>
  );
};

const TeamButton = ({ team, onSelect }) => (
  <button onClick={() => onSelect(team)}>{team.teamName}</button>
);

const PlayerButton = ({
  player,
  onSelect,
  isSelected,
  playerBelongsToUser,
}) => (
  <button
    onClick={() => onSelect(player)}
    style={{ backgroundColor: isSelected ? "green" : "grey" }}
  >
    {player.name}
  </button>
);

export default TradeAnalyzer;


import styled from 'styled-components';

export const TradeAnalyzerContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
  padding: 2rem;
  box-sizing: border-box;
  font-family: Arial, sans-serif;
`;

export const TeamsContainer = styled.div`
  display: flex;
  justify-content: space-between;
  width: 100%;
`;

export const Team = styled.div`
  display: flex;
  flex-direction: column;
  width: 48%;
  border: 1px solid #ddd;
  border-radius: 5px;
  padding: 1rem;
  box-sizing: border-box;
`;

export const TeamHeader = styled.h2`
  margin-bottom: 1rem;
  text-align: center;
  color: #444;
`;

export const PlayerButtonn = styled.button`
  margin-bottom: 0.5rem;
  padding: 0.5rem 1rem;
  border: none;
  border-radius: 5px;
  background-color: ${props => props.isSelected ? "#4caf50" : "#9e9e9e"};
  color: white;
  cursor: pointer;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: ${props => props.isSelected ? "#45a049" : "#707070"};
  }
`;

export const TeamButtonn = styled.button`
  margin-bottom: 1rem;
  padding: 0.5rem 1rem;
  border: none;
  border-radius: 5px;
  background-color: #3f51b5;
  color: white;
  cursor: pointer;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #303f9f;
  }
`;

export const SelectedPlayerContainer = styled.div`
  display: flex;
  flex-direction: column;
  width: 48%;
  margin-top: 2rem;
  border: 1px solid #ddd;
  border-radius: 5px;
  padding: 1rem;
  box-sizing: border-box;
`;

export const SelectedPlayerHeader = styled.h2`
  margin-bottom: 1rem;
  text-align: center;
  color: #444;
`;

export const SelectedPlayer = styled.p`
  margin-bottom: 0.5rem;
  color: #333;
`;
